import './App.css'
import { useDispatch, useSelector } from 'react-redux'
// 导入创action对象的方法
import { inscrement, descrement } from './store/modules/counterStore'


function App () {
  const { count } = useSelector(state => state.counter)
  // 得到dispatch函数
  const dispatch = useDispatch()
  // 点击+
  const handleIncrease = () => {
    dispatch(inscrement())
  }
  return (
    <div className="App">
      {/* 调用dispath提交action对象 */}
      <button onClick={() => dispatch(descrement())}>减少</button>
      <span>{count}</span>
      <button onClick={() => dispatch(inscrement())}>增加</button>
    </div>
  )
}

export default App
